string infixConvertToPostfix(string str){
    stack<char> stk;
    int n=str.size();
    string res="";
    char c=0;
    char top=0;
    for(int i=0;i<n;i++){
        c=str[i];
        if(c==' ') continue;
        if(c>='0'&&c<='9'){
            res.push_back(c);
        }else if(c== '+' || c== '-' || c == '*' || c == '/'){
            if(stk.empty()){
                stk.push(c);
            }else{
                while(!stk.empty()){
                    top=stk.top();
                    if(getPriority(top)>=getPriority(c)){
                        res.push_back(top);
                        stk.pop();
                    }else break;
                }
                stk.push(c);
            }
        }else{
            if(c=='(') stk.push(c);
            else{
                while (stk.top()!='(')
                {
                    res.push_back(stk.top());
                    stk.pop();
                }
                stk.pop();
            }
        }
    }
    //éå†å®Œåï¼Œè‹¥æ ˆéç©ºï¼Œå¼¹å‡ºæ‰€æœ‰å…ƒç´   
    while(!stk.empty()){
        res.push_back(stk.top());
        stk.pop();
    }
    return res;
}
