int calculatByInfix(string str){
    stack<int> stk;
    int n=str.size();
    int num1, num2, num3;  
    char c=0;
    for (int i = 0; i < n; i++) {  
        c=str[i];
        if (c >= '0' && c <= '9') {  
            stk.push(c - '0');  
        }  
        else {  
            num2 = stk.top();  
            stk.pop();  
            num1 = stk.top();  
            stk.pop();  
            if (c == '+') {  
                num3 = num1 + num2;  
            }  
            else if (c == '-') {  
                num3 = num1 - num2;  
            }  
            else if (c == '*') {  
                num3 = num1 * num2;  
            }  
            else if (c == '/') {  
                num3 = num1 / num2;  
            }  
            stk.push(num3);  
        }  
    }  
    return stk.top();  
}
