int getPriority(char ch)  
{  
    //è·å–ä¼˜å…ˆçº§  
    if (ch == '(') return 1;  
    else if (ch == '+' || ch == '-') return 2;  
    else if (ch == '*' || ch == '/') return 3;  
    else return 4;  
}  

void calculate(stack<double> &stk, char operation)  
{  
    double num1, num2, num3;  
    num2 = stk.top();  
    stk.pop();  
    num1 = stk.top();  
    stk.pop();  
    if (operation == '+') {  
        num3 = num1 + num2;  
    }  
    else if (operation == '-') {  
        num3 = num1 - num2;  
    }  
    else if (operation == '*') {  
        num3 = num1 * num2;  
    }  
    else if (operation == '/') {  
        num3 = num1 / num2;  
    }  

    stk.push(num3);  
}  

double calculator(string str)  
{  
    //è®¡ç®—ä¸­ç¼€è¡¨è¾¾å¼,é»˜è®¤è¾“å…¥æ˜¯åˆæ³•çš„  
    stack<double> stk_number;  
    stack<char> stk_operation;  
    int i = 0, j;  
    int size = str.size();  
    char tmp_operation;  
    string tmp_num;  
    while (i < size) {  
        if (str[i] >= '0' && str[i] <= '9') {  
            j = i;  
            while (j < size && str[j] >= '0' && str[j] <= '9') { j++; }  
            tmp_num = str.substr(i, j - i);  
            stk_number.push(atoi(tmp_num.c_str()));  
            i = j;  
        }  
        else if (str[i] == '+' || str[i] == '-' || str[i] == '*' || str[i] == '/') {  
            if (stk_operation.empty()) {  
                stk_operation.push(str[i]);  
            }  
            else {  
                while (!stk_operation.empty()) {  
                    tmp_operation = stk_operation.top();  
                    if (getPriority(tmp_operation) >= getPriority(str[i])) {  
                        //è®¡ç®—  
                        calculate(stk_number, tmp_operation);  
                        stk_operation.pop();  
                    }  
                    else break;  
                }  
                stk_operation.push(str[i]);  
            }  
            i++;  
        }  
        else {  
            if (str[i] == '(') stk_operation.push(str[i]);  
            else {  
                while (stk_operation.top() != '(') {  
                    tmp_operation = stk_operation.top();  
                    //è®¡ç®—  
                    calculate(stk_number, tmp_operation);  
                    stk_operation.pop();  
                }  
                stk_operation.pop();  
            }  
            i++;  
        }  

    }  
    //éå†å®Œåï¼Œè‹¥æ ˆéç©ºï¼Œå¼¹å‡ºæ‰€æœ‰å…ƒç´   
    while (!stk_operation.empty()) {  
        tmp_operation = stk_operation.top();  
        //è®¡ç®—  
        calculate(stk_number, tmp_operation);  
        stk_operation.pop();  
    }  
    return stk_number.top();  
}  
```

