class Solution
{
public:
    void solveSudoku(vector<vector<char>> &board)
    {
        dfs(board, 0);
    }

private:
    bool dfs(vector<vector<char>> &board, int index)
    {
        if (index > 80)
        {
            return true;
        }
        int x = index / 9;
        int y = index % 9;
        if (board[x][y] != '.')
        {
            return dfs(board, index + 1);
        }
        else
        {
            bool b = false;
            for (char i = '1'; i <= '9'; i++)
            {
                // éªŒè¯ièƒ½ä¸èƒ½å¡«è¿™é‡Œ
                bool flag = true;
                for (int k = 0; k < 9; k++)
                {
                    if (board[x][k] == i || board[k][y] == i)
                    {
                        flag = false;
                        break;
                    }
                }
                if (flag)
                {
                    int posx = x / 3;
                    int posy = y / 3;
                    for (int j = 0; j < 3; j++)
                    {
                        for (int k = 0; k < 3; k++)
                        {
                            if (board[posx * 3 + j][posy * 3 + k] == i)
                            {
                                flag = false;
                                break;
                            }
                        }
                    }
                }
                if (flag)
                {
                    b = true;
                    board[x][y] = i;
                    if (!dfs(board, index + 1))
                    {
                        board[x][y] = '.';
                    }
                    else
                    {
                        return true;
                    }
                }
            }
            if (!b)
            {
                return false;
            }
        }
        return false;
    }
};
```

