class Solution {
public:
    int findContentChildren(vector<int>& g, vector<int>& s) {
        int res=0;
        int n=g.size();
        sort(g.begin(),g.end());sort(s.begin(),s.end());
        int index=0;
        int m=s.size();
        for(int i=0;i<n;i++){
            while(index<m&&s[index]<g[i]) index++;
            if(index<m) {
                res++;
                index++;
            }
            else break;
        }
        return res;
    }
};
```

