class Solution {
public:
    bool isTrangle(int a,int b,int c){
        return a+b>c&&b+c>a&&a+c>b;
    }
    int largestPerimeter(vector<int>& nums) {
        sort(nums.begin(),nums.end());
        int res=0;
        int n=nums.size();
        for(int i=n-1;i>=2;i--){
            for(int j=i-1;j>=1;j--){
                int hi=nums[i]+nums[j]-1;
                int lo=nums[i]-nums[j]+1;
                if(nums[j-1]<lo||nums[0]>hi) continue;
                else{
                    res=max(res,nums[i]+nums[j]+nums[j-1]);
                }
            }
        }
        return res;
    }
};
```

