class Solution {
public:
    int largestSumAfterKNegations(vector<int>& nums, int k) {
        int sum=0;
        vector<int> negative;
        int min_postive=INT_MAX;
        for(int i=0;i<nums.size();i++){
            if(nums[i]<0) negative.push_back(nums[i]);
            else if(nums[i]<min_postive) min_postive=nums[i];
            sum+=nums[i];
        }
        sort(negative.begin(),negative.end());
        int index=0;
        while(k&&index<negative.size()){
            negative[index]=-negative[index];
            sum+=2*negative[index];
            index++;
            k--;
        }
        if(k%2){
            if(negative.size()>0){
                min_postive=min(min_postive,negative[negative.size()-1]);
            }
            sum-=2*min_postive;
        }
        return sum;
    }
};
```

