class Solution {
public:
    bool canThreePartsEqualSum(vector<int>& arr) {
        int n=arr.size();
        int sum=0;
        for(int i=0;i<n;i++){
            sum+=arr[i];
        }   
        if(sum%3!=0) return false; 
        int left=0;
        int right=n-1;
        int s0=0,s1=0;
        while(left<n){
            s0+=arr[left];
            if(s0==sum/3){
                break;
            }
            left++;
        }
        while(right>=0){
            s1+=arr[right];
            if(s1==sum/3){
                break;
            }
            right--;
        }
        return right>left+1;
    }
};
```

