class Solution {
public:
    int canCompleteCircuit(vector<int>& gas, vector<int>& cost) {
        int n=gas.size();
        set<int> st;
        int begin_index=-1;
        for(int i=0;i<n;i++){
            if(gas[i]-cost[i]>=0) {
                begin_index=i;
                break;
            }
        }
        if(begin_index==-1) return -1;
        int bug_index=-1;
        st.insert(begin_index);
        while(begin_index<n){
            int total=gas[begin_index]-cost[begin_index];
            for(int j=(begin_index+1)%n;j!=begin_index;j=(j+1)%n){
                total+=(gas[j]-cost[j]);
                if(total<0) {
                    bug_index=j;
                    break;
                }
            }
            if(total>=0) return begin_index;
            else{
                int k;
                for(k=bug_index;;k=(k+1)%n){
                    if(gas[k]>=cost[k]) {
                        break;
                    }
                }
                begin_index=k;
                if(st.count(begin_index)) return -1;
                else st.insert(begin_index);
            }
        }
        return -1;
    }
};
```

