class Solution {
private:
    bool isOk(string& s, int index, char now, char change) {
        s[index]=change;
        int begin=(index>=2?index-2:0);
        bool flag=(s[begin  ]==change&&s[begin+1]==change&&s[begin+2]==change)||
                  (s[begin+1]==change&&s[begin+2]==change&&s[begin+3]==change)||
                  (s[begin+2]==change&&s[begin+3]==change&&s[begin+4]==change);
        s[index]=now;
        return !flag;
    }

public:
    string strWithout3a3b(int a, int b) {
        string res = "";
        char before = 'a';
        if (a > b)
            before = 'a';
        else
            before = 'b';
        for (int i = 0; i < a + b; i++)
            res += before;
        char after = (before == 'a' ? 'b' : 'a');
        int cnt = (after == 'a' ? a : b);
        for (int i = 2; i < a + b && cnt;) {
            res[i] = after;
            i += 3;
            cnt--;
        }
        if (cnt) {
            for (int i = 0; i < a + b && cnt; i++) {
                if (res[i] == after) {
                    continue;
                }
                if (isOk(res, i, res[i], after)) {
                    res[i]=after;
                    cnt--;
                }
            }
        }
        return res;
    }
};
