#define min_(a,b,c) min(min((a),(b)),(c))

class Solution {
public:
    int minFallingPathSum(vector<vector<int>>& matrix) {
        int n=matrix.size();
        vector<vector<int>> dp(n,vector<int>(n));
        for(int i=0;i<n;i++) dp[0][i]=matrix[0][i];
        for(int i=1;i<n;i++){
            for(int j=0;j<n;j++) { 
                dp[i][j]=min_(j==0?INT_MAX:dp[i-1][j-1],dp[i-1][j],j==n-1?INT_MAX:dp[i-1][j+1])+matrix[i][j];
            }
        }
        int res=INT_MAX;
        for(int i=0;i<n;i++) res=min(res,dp[n-1][i]);
        return res;
    }
};
