class Solution {
public:
    bool isT(int n){
        int i= (int)sqrt(n);
        return i*i==n;
    }
    int numSquares(int n) {
        if(n<4) return n;
        if(isT(n)) return 1;
        vector<int> dp(n+1,0);
        dp[0]=0;
        dp[1]=1;
        dp[2]=2;
        dp[3]=3;
        for(int i=4;i<=n;i++){
            if(isT(i)) dp[i]=1;
            else{
                int mincnt=INT_MAX;
                for(int t=1;i-t*t>0;t++){
                    mincnt=min(dp[i-t*t]+1,mincnt);
                }
                dp[i]=mincnt;
            }
        }
        return dp[n];
    }
};
```

