#include<iostream>
#include<vector>
#include<string>

using namespace std;

struct TrirNode
{
    bool isWord;
    vector<TrirNode*> children;
    TrirNode(): children(26) ,isWord(false) {}
}*trie;

void createTrie(){
    trie=new TrirNode();
}

void insertHelper(string& word,int index,TrirNode* node){
    if(index==word.size()) return;
    int i=word[index]-'a';
    if(node->children[i]==nullptr){
        node->children[i]=new TrirNode();
    }
    if(node->children[i]->isWord==false) node->children[i]->isWord=(index==word.size()-1);
    insertHelper(word,index+1,node->children[i]);
}

void insert(string& word){
    if(word.size()==0) return;
    insertHelper(word,0,trie);
}

int res=0;

int findHeight(TrirNode* node){
    if(node==nullptr) return 0;
    int maxHeight=0;
    for(int i=0;i<node->children.size();i++){
        maxHeight=max(maxHeight,findHeight(node->children[i]));
    }
    return maxHeight+(node->isWord?1:0);
}

void findMaxHeight(){
    res=findHeight(trie);
}

int main(){
    int n;
    string word;
    cin>>n;
    createTrie();
    while (n--)
    {
        cin>>word;
        insert(word);
    }
    findMaxHeight();
    cout<<res<<endl;
    return 0;
}
