class MyHashSet {
    private final int DEFAULT_SIZE=10;

    private Item[] items;

    public MyHashSet() {
        items=new Item[DEFAULT_SIZE];
    }

    public void add(int key) {
        if(contains(key)) return;
        int index=key%DEFAULT_SIZE;
        if(items[index]==null){
            items[index]=new Item();
            items[index].value=key;
        }else{
            if(items[index].list==null){
                items[index].list=new LinkedList();
            }
            items[index].list.add(key);
        }
    }

    public void remove(int key) {
        int index=key%DEFAULT_SIZE;
        if(items[index]==null) return;
        if(items[index].value==key){
            List<Integer> list = items[index].list;
            if(list==null||list.isEmpty()){
                items[index]=null;
            }else{
                int lastValue=list.get(list.size()-1);
                items[index].value=lastValue;
                list.remove(list.size()-1);
                if(list.isEmpty()) items[index].list=null;
            }
        }else{
            List<Integer> list=items[index].list;
            if(list==null) return;
            int findIndex=-1;
            for (int i = 0; i < list.size(); i++) {
                if(list.get(i).equals(key)){
                    findIndex=i;
                }
            }
            if(findIndex!=-1){
                list.remove(findIndex);
                if(list.isEmpty()) items[index].list=null;
            }
        }
    }

    public boolean contains(int key) {
        int index=key%DEFAULT_SIZE;
        if(items[index]==null) return false;
        else{
            if(items[index].value==key) return true;
            List<Integer> list = items[index].list;
            if(list==null) return false;
            for (int i = 0; i < list.size(); i++) {
                if(list.get(i).equals(key)) return true;
            }
            return false;
        }
    }

    public static void main(String[] args) {
        MyHashSet myHashSet = new MyHashSet();
        myHashSet.add(1);      // set = [1]
        myHashSet.add(2);      // set = [1, 2]
        myHashSet.contains(1); // è¿”å› True
        myHashSet.contains(3); // è¿”å› False ï¼Œï¼ˆæœªæ‰¾åˆ°ï¼‰
        myHashSet.add(2);      // set = [1, 2]
        myHashSet.contains(2); // è¿”å› True
        myHashSet.remove(2);   // set = [1]
        myHashSet.contains(2); // è¿”å› False ï¼Œï¼ˆå·²ç§»é™¤ï¼‰
    }
}

class Item{
    int value;
    List<Integer> list;
}
