class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        vector<int> res;
        int len=p.size();
        int n=s.size();
        vector<int> count(26,0);
        for(int i=0;i<len;i++){
            count[p[i]-'a']++;
        }
        vector<int> cnt(26,0);
        for(int i=0;i<=n-len;i++){
            if(i==0){
                for(int j=0;j<len;j++){
                    cnt[s[i+j]-'a']++;
                }
            }
            bool flag=true;
            for(int k=0;k<26;k++){
                if(cnt[k]!=count[k]) {
                    flag=false;
                    break;
                }
            }
            if(flag){
                res.push_back(i);
            }
            if(i!=n-len){
                cnt[s[i]-'a']--;
            cnt[s[i+len]-'a']++;
            }
        }
        return res;
    }
};
```

