class Solution {
public:
    vector<int> productExceptSelf(vector<int>& nums) {
        int n=nums.size();
        vector<int> prefix(n,1);
        vector<int> endfix(n,1);
        prefix[0]=nums[0];
        for(int i=1;i<n;i++){
           prefix[i]=prefix[i-1]*nums[i];
        }
        endfix[n-1]=nums[n-1];
        for(int i=n-2;i>=0;i--){
            endfix[i]=endfix[i+1]*nums[i];
        }
        vector<int> ans(n,0);
        for(int i=0;i<n;i++){
            ans[i]=(i>0?prefix[i-1]:1)*(i<n-1?endfix[i+1]:1);
        }
        return ans;
    }
};
```

