class Solution {
public:
    int m,n;
    int dir[4][2]={
        {-1,0},
        {1,0},
        {0,-1},
        {0,1}
    };
    int numIslands(vector<vector<char>>& grid) {
        m=grid.size();
        n=grid[0].size();
        int ans=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]=='1'){
                    ans++;
                    dfs(grid,i,j);
                }
            }
        }
        return ans;
    }
    void dfs(vector<vector<char>> &grid,int row,int col){
        if(row<0||row>=m||col<0||col>=n) return;
        if(grid[row][col]=='0') return;
        grid[row][col]='0';
        for(int i=0;i<4;i++){
            int nx=row+dir[i][0];
            int ny=col+dir[i][1];
            dfs(grid,nx,ny);
        }
    }
};
```

