int slove(vector<int> &nums,int k){
    vector<vector<int>> numskradix(nums.size(),vector<int>());
    //æ±‚æ¯ä¸ªæ•°çš„kè¿›åˆ¶è¡¨ç¤º
    int maxlen=0;
    for(int i=0;i<nums.size();i++){
        int t=nums[i];
        while(t){
            numskradix[i].push_back(t%k);
            t=t/k;
        }
        maxlen=max(maxlen,numskradix[i].size());
    }

    //è®¡ç®—
    vector<int> arr(maxlen,0);
    for(int i=0;i<maxlen;i++){
        for(int j=0;j<nums.size();j++){
            if(i<numskradix[j].size()){
                arr[i]=(arr[i]+numskradix[j][i])%k;
            }
        }
    }

    //å¤åŸ
    int ans=0;
    int m=1;
    for(int i=0;i<maxlen;i++){
        ans+=arr[i]*m;
        m=m*k;
    }
    return ans;
}
```

