#include<iostream>
#include<vector>

using namespace std;
bool func(int n,int s,int m,vector<pair<int,int>> &nums){
    int before=0;
    if(m-nums[n-1].second>=s) return true;
    for(int i=0;i<n;i++){
        if(nums[i].first-before>=s) return true;
        else{
            before=nums[i].second;
        }
    }
    return false;
}
int main(){
    int t;
    cin>>t;
    int a;
    int n,s,m;
    while(t--){
        cin>>n>>s>>m;
        vector<pair<int,int>> nums(n);
        for(int i=0;i<n;i++){
            cin>>nums[i].first>>nums[i].second;
        }
        if(func(n,s,m,nums)){
            cout<<"YES"<<endl;
        }else{
            cout<<"NO"<<endl;
        }
    }
    return 0;
}
