#include<string>
#include<vector>
#include<algorithm>
#include<map>

class Solution {
public:
    vector<vector<string>> groupAnagrams(vector<string>& strs) {
        vector<vector<string>> ans;
        map<string,int> mp;
        for(int i=0;i<strs.size();i++){
            string ss=sort_(strs[i]);
            auto fd=mp.find(ss);
            if(fd!=mp.end()){
                int index=mp[ss];
                ans[index].push_back(strs[i]);
            }else{
                mp[ss]=ans.size();
                ans.push_back(vector<string>(1,strs[i]));
            }
        }
        return ans;
    }

    string sort_(string str){
        sort(str.begin(),str.end());
        return str;
    }
};
