#include<vector>
#include<set>
class Solution {
public:
    int longestConsecutive(vector<int>& nums) {
        set<int> st;
        int n=nums.size();
        for(int i=0;i<n;i++){
            st.insert(nums[i]);
        }
        int ans=0;
        for(int num:st){
            if(!st.count(num-1)){
                int cur=num;
                int len=1;
                while(st.count(cur+1)){
                    cur++;
                    len++;
                }
                ans=max(ans,len);
            }
        }
        return ans;
    }
};
