class Solution {
public:
    int firstMissingPositive(vector<int>& nums) {
        set<int> st;
        for (int num:nums) {
            if(num>0) st.insert(num);
        }
        int res=0;
        for(int num:st){
            if(num==res+1) res++;
            else return res+1;
        }
        return res+1;
    }
};
