#include <iostream>
#include <vector>
#include <stack>
using namespace std;
void solve(int n) {
    stack<int> stk;
    vector<bool> nums(n, true);
    int count = 0;
    int index = 0;
    while (stk.size() < n) {
        if (nums[index] && count == 2) {
            nums[index] = false;
            stk.push(index);
            count = 0;
            index = (index + 1) % n;
        } else if (!nums[index]) {
            index = (index + 1) % n;
        } else {
            index = (index + 1) % n;
            count++;
        }
    }
    cout << stk.top() << endl;
}
int main() {
    int n;
    while(cin>>n){
        solve(n);
    }
    return 0;
}
