#include <iostream>
#include <vector>
#include <set>
#define SIZE 9

using namespace std;

vector<vector<int>> map(SIZE, vector<int>(SIZE));
// int cnt = 0;
int notzerocnt = 0;
bool check(int row, int col, int num)
{
    for (int i = 0; i < SIZE; i++)
    {
        if (map[row][i] == num || map[i][col] == num)
            return false;
    }
    int rb = (row / 3) * 3;
    int cb = (col / 3) * 3;
    for (int i = rb; i < rb + 3; i++)
    {
        for (int j = cb; j < cb + 3; j++)
        {
            if (map[i][j] == num)
                return false;
        }
    }
    return true;
}
void print()
{
    for (int i = 0; i < 9; i++)
    {
        for (int j = 0; j < 9; j++)
        {
            cout << map[i][j] << " ";
        }
        cout << endl;
    }
}
void backtrace(int n, int notzerocnt)
{
    int row = n / 9;
    int col = n % 9;
    if (n == 81 || notzerocnt == 81)
    {
        print();
        return;
    }
    if (map[row][col] != 0)
    {

        if (col < SIZE - 1)
        {
            backtrace(++n, notzerocnt);
        }
        else
        {
            backtrace(++n, notzerocnt);
        }
    }
    else
    {
        for (int i = 1; i <= SIZE; i++)
        {

            if (check(row, col, i))
            {
                map[row][col]=i;
                backtrace(n+1, notzerocnt+1);
                map[row][col]=0;
            }
        }
    }
}
int main()
{

    for (int i = 0; i < SIZE; i++)
    {
        for (int j = 0; j < SIZE; j++)
        {
            cin >> map[i][j];
            if (map[i][j] != 0)
                notzerocnt++;
        }
    }
    backtrace(0, notzerocnt);
}
