```java
import java.util.*;

/**
 * äºŒå‰æ£€ç´¢æ ‘ï¼šå¯¹ä»»æ„ä¸€ä¸ªèŠ‚ç‚¹ï¼Œ
 *      å·¦å­æ ‘ä¸Šçš„èŠ‚ç‚¹çš„å€¼å‡å°äºè¯¥èŠ‚ç‚¹çš„å€¼
 *      å³å­æ ‘ä¸Šçš„èŠ‚ç‚¹çš„å€¼å‡ä¸å°äºè¯¥èŠ‚ç‚¹çš„å€¼
 */
public class MyBST<K extends Comparable<K> ,V> implements BST<K,V>{

    private BinNode<K,V> root;
    public MyBST(){
        root=null;
    }
    @Override
    public void insert(K key, V value) {
        if (key==null){
            try {
                throw new Exception("key is null.");
            } catch (Exception e) {
                e.printStackTrace();
            }
            return;
        }
        root=insertHelper(key,value,root);
    }

    private BinNode<K,V> insertHelper(K key,V value,BinNode<K,V> root){
        if (root==null){
            return new BinNode<>(key,value);
        }
        int res=root.getKey().compareTo(key);
        if (res<=0){//rootæ›´å° æ’å…¥å³å­æ ‘
            root.setRight(insertHelper(key,value,root.getRight()));
        }else{
            root.setLeft(insertHelper(key,value,root.getLeft()));
        }
        return root;
    }

    @Override
    public V remove(K key) {
        if (key==null) {
            try {
                throw new Exception("key is null.");
            } catch (Exception e) {
                e.printStackTrace();
            }
            return null;
        }
        V val=search(key);
        if (val!=null) removeHelper(key,root);
        return val;
    }

    private BinNode<K,V> findMin(BinNode<K,V> root){
        if (root==null) return null;
        if (root.getLeft()==null) return root;
        return findMin(root.getRight());
    }

    private BinNode<K,V> removeHelper(K key,BinNode<K,V> root){
        if (root==null) return null;
        int res=root.getKey().compareTo(key);
        if (res>0){
            root.setLeft(removeHelper(key,root.getLeft()));
        } else if (res<0) {
            root.setRight(removeHelper(key,root.getRight()));
        }else{
            //æ°å¥½æ˜¯è¿™ä¸ªèŠ‚ç‚¹éœ€è¦åˆ é™¤
            V val=root.getValue();
            if (root.getLeft()==null){
                root=root.getRight();
            }else if (root.getRight()==null){
                root=root.getLeft();
            }else{
                //æ‰¾åˆ°å³å­æ ‘ä¸­æœ€å¤§çš„èŠ‚ç‚¹
                BinNode<K, V> min = findMin(root);
                root.setKey(min.getKey());
                root.setValue(min.getValue());
                root.setRight(min);
            }
        }
        return root;
    }

    private void deleteMaxHelper(BinNode<K,V> root){
        if (root==null) return;
        if (root.getRight()==null) return;
        if (root.getRight().getRight()==null){
            root.getRight().setRight(null);
        }else{
            deleteMaxHelper(root.getRight());
        }
    }
    @Override
    public V search(K key) {
        if (key==null) {
            try {
                throw new Exception("key is null.");
            } catch (Exception e) {
                e.printStackTrace();
            }
            return null;
        }
        return searchHelper(key,root);
    }

    private V searchHelper(K key, BinNode<K,V> root){
        if (root==null) return null;
        int res=root.getKey().compareTo(key);
        if (res==0) return root.getValue();
        else if (res>0) return searchHelper(key,root.getLeft());
        else return searchHelper(key,root.getRight());
    }

    @Override
    public boolean update(K key, V value) {
        if (key==null) {
