 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
#include<string>
#include<vector>
#include<stack>
class Solution {
public:
    vector<string> ans;
    vector<string> binaryTreePaths(TreeNode* root) {
        vector<int> stk;
        stk.push_back(root->val);
        dfs(root,stk);
        return ans;
    }
    void dfs(TreeNode* root, vector<int>& stk){
        if(isleaf(root)!=0){
            ans.push_back(copy(stk));
            return;
        }
        if(root->left==nullptr){
            stk.push_back(root->right->val);
            dfs(root->right,stk);
            stk.pop_back();
        }else if(root->right==nullptr){
            stk.push_back(root->left->val);
            dfs(root->left,stk);
            stk.pop_back();
        }else {
            stk.push_back(root->left->val);
            dfs(root->left,stk);
            stk.pop_back();

            stk.push_back(root->right->val);
            dfs(root->right,stk);
            stk.pop_back();
        }
    }
    string copy(vector<int>& stk){
        string ans="";
        for(int i=0;i<stk.size();i++){
            if(i!=0) ans+="->";
            ans+=to_string(stk[i]);
        }
        return ans;
    }
    int isleaf(TreeNode* root){
        if(root==nullptr) return -1;
        if(root->right==nullptr&&root->left==nullptr) return 1;
        return 0;
    }

};
