typedef unsigned long long ll;
ll pingjie(ll a, ll b)
{
    ll x = b;
    if (x == 0)
        return a * 10;
    while (x > 0)
    {
        a = a * 10;
        x /= 10;
    }
    a += b;
    return a;
}
bool cmp(int a, int b)
{
    ll ab = pingjie(a, b);
    ll ba = pingjie(b, a);
    return ab > ba;
}

