class Solution {
public:
    long long distributeCandies(int n, int limit) {
        if(limit<n/3) return 0;
        long long ans=0;
        for(int i=max(0,n-2*limit);i<=limit&&n-i>=0;i++){
            int k=n-i;
            if(k<limit) ans+=k+1;
            else ans+=limit-(k-limit)+1;
        }
        return ans;
    }
};
