class Solution {
public:
    int removeDuplicates(vector<int>& nums) {
        int n=nums.size();
        int cnt=0;
        for(int i=0;i<n;i++){
            if(i>0&& nums[i]!=nums[i-1]) cnt=1;
            else cnt++;
            if(cnt==3){
                remove(nums,i,n);
                n--;
                cnt--;
                i--;
            }
        }
        return n;
    }
    void remove(vector<int>& nums,int index,int end){
        for(int i=index;i<end-1;i++){
            nums[i]=nums[i+1];
        }
    }
};
