private static void integerite(Element[] elements,int left1,int right1,int left2,int right2){
    int index1=left1;
    int index2=left2;
    int currIndex=left1;
    while (currIndex<=right2){
        int compare= 0;
        if (index1>right1) compare=1;
        else if (index2>right2) compare=-1;
        else compare=elements[index1].getKey()-elements[index2].getKey();
        if (compare>0){
            elements[currIndex++]=elements[index2++];
        } else {
            elements[currIndex++]=elements[index1++];
        }
    }
}

private static void mergeSortHelper(Element[] elements,int left,int right){
    if (left>=right) return;
    int mid=(left+right)/2;
    mergeSortHelper(elements,left,mid);
    mergeSortHelper(elements,mid+1,right);
    integerite(elements,left,mid,mid+1,right);
}

public static void mergeSort(Element[] elements){
    mergeSortHelper(elements,0,elements.length-1);
}
