```cpp
void Sort::quick_sort(vector<int> &nums) {
    quick_sort_helper(nums,0,nums.size()-1);
}

void Sort::quick_sort_helper(vector<int> &nums,
                             int start, int end) {
    if (start>=end) return;
    int pivot=nums[start];
    int left=start;
    int right=end;
    while (left<right){
        //å…ˆç§»åŠ¨right
        while (right>left&&nums[right]>=pivot){
            right--;
        }
        while (right>left&&nums[left]<=pivot){
            left++;
        }
        swap(nums[left],nums[right]);
    }
    nums[right]=pivot;
    quick_sort_helper(nums,start,right-1);
    quick_sort_helper(nums,right+1,end);
}
```
